import { createStore } from "redux";

function reducer(state = {
  data: [
    {
      id: 0,
      content: "读书",
      done: false
    }, {
      id: 1,
      content: "写字",
      done: true
    }
  ]
}, action) {
  let nowData = [...state.data];
  switch (action.type) {
    case "DONE":
      nowData.forEach((item, index) => {
        // 通过 id 找到该条数据
        if(item.id === action.id) {
          nowData[index] = {
            ...item,
            done: action.done
          }
          console.log("当前勾选状态",item.done);
        }
      })
    
  }


  return state;
}
const store = createStore(reducer);
export { store };
