import { createStore } from "redux";
function reducer(state = {
  data: [
    {
      id: 0,
      title: xxx,
      selected: false
    }, {
      id: 1,
      title: xxxx,
      selected: false
    }
  ]
}, action) {
  let nowData = [...state.data];
  switch (action.type) {
    case "ADD":
      return {
        data: [...nowData, {
          id: Date.now(),
          name: action.name,
          message: action.message,
          selected: false
        }]
      }
    case "SELECTED":
      nowData.forEach((item, index) => {
        if (item.id === action.id) {
          nowData[index] = {
            ...item,
            selected: action.selected
          }
        }
      })
      return { data: nowData };
    case "EDIT":
      nowData.forEach((item, index) => {
        if (item.id === action.id) {
          nowData[index] = {
            ...item,
            message: action.message
          }
        }
      })
      return {
        data: nowData
      }

  }
  return state;
}
const store = createStore(reducer);
export { store };
