import { createStore } from "redux";

function reducer(state = {
  data: [
    {
      id: 0,
      title: "xxx",
      selected: false
    }, {
      id: 1,
      title: "xxxx",
      selected: true
    }
  ]
}, action) {

  return state;
}
const store = createStore(reducer);
export { store };
